import React, { useState, useEffect } from 'react';
import "../css/studentDashboard.css"; // Create this CSS file for styling

function StudentDashboard() {
    const [user, setUser] = useState(null);
    const [selectedDay, setSelectedDay] = useState("Monday");
    const [selectedTime, setSelectedTime] = useState("08:00 AM - 08:50 AM");

    useEffect(() => {
        const token = localStorage.getItem("token");
        if (token) {
            try {
                const payload = JSON.parse(decodeURIComponent(escape(atob(token.split(".")[1])))); 
                setUser(payload);
            } catch (error) {
                console.error("Error decoding token:", error);
            }
        }
    }, []);

    const days = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday"];
    const timeSlots = [
        "08:00 AM - 08:50 AM", "09:00 AM - 09:50 AM", "10:00 AM - 10:50 AM", 
        "11:00 AM - 11:50 AM", "12:00 PM - 12:50 PM",
        "02:00 PM - 02:50 PM", "03:00 PM - 03:50 PM", "04:00 PM - 04:50 PM", 
        "05:00 PM - 05:50 PM", "06:00 PM - 06:50 PM"
    ];

    const handleSearch = () => {
        alert(`Searching for: ${selectedDay}, ${selectedTime}`);
    };

    return (
        <div className="dashboard-container">
            <h1>Student Dashboard</h1>
            {user ? <h2>Welcome, {user.name}!</h2> : <p>Loading...</p>}

            {/* Selection Form */}
            <div className="selection-container">
                <label htmlFor="day">Day:</label>
                <select id="day" value={selectedDay} onChange={(e) => setSelectedDay(e.target.value)}>
                    {days.map((day, index) => (
                        <option key={index} value={day}>{day}</option>
                    ))}
                </select>

                <label htmlFor="time">Time:</label>
                <select id="time" value={selectedTime} onChange={(e) => setSelectedTime(e.target.value)}>
                    {timeSlots.map((time, index) => (
                        <option key={index} value={time}>{time}</option>
                    ))}
                </select>

                <button className="search-button" onClick={handleSearch}>Search</button>
            </div>
        </div>
    );
}

export default StudentDashboard;
