import React, { useState } from "react";
import axios from "axios";
import "../css/facultyDashboard.css";

function FacultyDashboard() {
  const [selectedDay, setSelectedDay] = useState("Monday");
  const [selectedTime, setSelectedTime] = useState("8:00 AM - 8:50 AM");
  const [selectedBlocks, setSelectedBlocks] = useState([]);
  const [availableRooms, setAvailableRooms] = useState([]);
  const [filteredRooms, setFilteredRooms] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  // Modal state
  const [showModal, setShowModal] = useState(false);
  const [selectedRoom, setSelectedRoom] = useState(null);

  // Booking feedback
  const [bookingMessage, setBookingMessage] = useState("");

  const days = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday"];
  const timeSlots = [
    "8:00 AM - 8:50 AM",
    "9:00 AM - 9:50 AM",
    "10:00 AM - 10:50 AM",
    "11:00 AM - 11:50 AM",
    "12:00 PM - 12:50 PM",
    "2:00 PM - 2:50 PM",
    "3:00 PM - 3:50 PM",
    "4:00 PM - 4:50 PM",
    "5:00 PM - 5:50 PM",
    "6:00 PM - 6:50 PM",
  ];

  const blocks = ["SJT", "TT", "MGB", "ALM", "CDMM", "GDN", "PRP", "CTS", "SMV", "MB"];

  const handleBlockCheckboxChange = (block) => {
    if (selectedBlocks.includes(block)) {
      setSelectedBlocks(selectedBlocks.filter((b) => b !== block));
    } else {
      setSelectedBlocks([...selectedBlocks, block]);
    }
  };

  const fetchRooms = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/classrooms/available-rooms", {
        params: { day: selectedDay, time: selectedTime },
      });

      const allRooms = res.data;

      const filtered = selectedBlocks.length > 0
        ? allRooms.filter((room) => {
            const roomStr = (room["ROOM NUMBER"] || room.roomNumber || "").toUpperCase();
            const blockMatch = roomStr.match(/^[A-Z]+/);
            const block = blockMatch ? blockMatch[0] : "";
            return selectedBlocks.includes(block);
          })
        : allRooms;

      setAvailableRooms(allRooms);
      setFilteredRooms(filtered);
      setBookingMessage(""); // Clear any old message on new search
    } catch (error) {
      console.error("Error fetching available rooms:", error);
      setAvailableRooms([]);
      setFilteredRooms([]);
    }
  };

  const handleReset = () => {
    setSelectedDay("Monday");
    setSelectedTime("8:00 AM - 8:50 AM");
    setSelectedBlocks([]);
    setSearchTerm("");
    setAvailableRooms([]);
    setFilteredRooms([]);
    setBookingMessage("");
  };

  const groupedRooms = filteredRooms.reduce((acc, room) => {
    const roomStr = (room["ROOM NUMBER"] || room.roomNumber || "").toUpperCase();
    const blockMatch = roomStr.match(/^[A-Z]+/);
    const block = blockMatch ? blockMatch[0] : "Others";
    if (!acc[block]) acc[block] = [];
    if (roomStr.toLowerCase().includes(searchTerm.toLowerCase())) {
      acc[block].push(room);
    }
    return acc;
  }, {});

  const handleRoomClick = (room) => {
    setSelectedRoom(room);
    setShowModal(true);
  };

  const confirmBooking = () => {
    const roomNumber = selectedRoom["ROOM NUMBER"] || selectedRoom.roomNumber;
    setBookingMessage(`Booking request made for ${roomNumber}. Please wait for approval or rejection through mail.`);
    setShowModal(false);
    setSelectedRoom(null);
  };

  const cancelBooking = () => {
    setShowModal(false);
    setSelectedRoom(null);
  };

  return (
    <div className="faculty-dashboard">
      <div className="dashboard-container">
        <h1>Faculty Dashboard</h1>

        {/* Dropdown Selection */}
        <div className="selection-container">
          <label htmlFor="day">Day:</label>
          <select id="day" value={selectedDay} onChange={(e) => setSelectedDay(e.target.value)}>
            {days.map((day, idx) => (
              <option key={idx} value={day}>
                {day}
              </option>
            ))}
          </select>

          <label htmlFor="time">Time:</label>
          <select id="time" value={selectedTime} onChange={(e) => setSelectedTime(e.target.value)}>
            {timeSlots.map((slot, idx) => (
              <option key={idx} value={slot}>
                {slot}
              </option>
            ))}
          </select>

          <button className="search-button" onClick={fetchRooms}>Search</button>
          <button className="reset-button" onClick={handleReset}>Reset</button>
        </div>

        {/* Block Checkboxes */}
        <div className="block-filter">
          {blocks.map((block, idx) => (
            <label key={idx} className="block-checkbox">
              <input
                type="checkbox"
                checked={selectedBlocks.includes(block)}
                onChange={() => handleBlockCheckboxChange(block)}
              />
              {block}
            </label>
          ))}
        </div>

        {/* Search Bar */}
        <div className="search-bar-container">
          <input
            type="text"
            className="room-search-input"
            placeholder="Search room number..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        {/* Grouped Room Results */}
        <div className="room-container">
          <h3>Available Rooms</h3>
          {Object.entries(groupedRooms)
            .filter(([block, rooms]) => rooms.length > 0)
            .map(([block, rooms]) => (
              <div key={block} className="block-group">
                <h4>{block}</h4>
                <div className="room-grid">
                  {rooms.map((room, i) => (
                    <button
                      key={i}
                      className="room-box"
                      onClick={() => handleRoomClick(room)}
                    >
                      {room["ROOM NUMBER"] || room.roomNumber || "No Room Name"}
                    </button>
                  ))}
                </div>
              </div>
            ))}

          {Object.keys(groupedRooms).length === 0 && (
            <p>No rooms available for this slot.</p>
          )}
        </div>

        {/* ✅ Booking Message */}
        {bookingMessage && (
          <div className="booking-message">
            <p>{bookingMessage}</p>
          </div>
        )}
      </div>

      {/* Modal Confirmation */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <p>
              Do you want to book{" "}
              <strong>{selectedRoom?.["ROOM NUMBER"] || selectedRoom?.roomNumber}</strong>?
            </p>
            <div className="modal-actions">
              <button className="confirm-button" onClick={confirmBooking}>Book</button>
              <button className="cancel-button" onClick={cancelBooking}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default FacultyDashboard;
