import React, { useState, useEffect } from "react";
import axios from "axios";
import "../css/studentDashboard.css";

const handleRoomClick = (room) => {
    console.log("Room Clicked:", room);
    alert(`You clicked on ${room["ROOM NUMBER"] || room.roomNumber}`);
};

function StudentDashboard() {
  const [user, setUser] = useState(null);
  const [selectedDay, setSelectedDay] = useState("Monday");
  const [selectedTime, setSelectedTime] = useState("08:00 AM - 08:50 AM");
  const [availableRooms, setAvailableRooms] = useState([]); // Store rooms

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      try {
        const payload = JSON.parse(
          decodeURIComponent(escape(atob(token.split(".")[1])))
        );
        setUser(payload);
      } catch (error) {
        console.error("Error decoding token:", error);
      }
    }
  }, []);

  const days = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday"];
  const timeSlots = [
    "8:00 AM - 8:50 AM",
    "9:00 AM - 9:50 AM",
    "10:00 AM - 10:50 AM",
    "11:00 AM - 11:50 AM",
    "12:00 PM - 12:50 PM",
    "2:00 PM - 2:50 PM",
    "3:00 PM - 3:50 PM",
    "4:00 PM - 4:50 PM",
    "5:00 PM - 5:50 PM",
    "6:00 PM - 6:50 PM",
  ];

  const formatTime = (time) => {
    return time.replace(/^0(\d)/, "$1"); // Remove leading zero from hour part
  };

  const handleSearch = async () => {
    console.log("Search button clicked");
    console.log("Selected Day:", selectedDay);
    console.log("Selected Time (Before Formatting):", selectedTime);

    const formattedTime = formatTime(selectedTime);
    console.log("Formatted Time (No Leading Zero on Hour Only):", formattedTime);

    try {
      const response = await axios.get(
        "http://localhost:5000/api/classrooms/available-rooms",
        {
          params: { day: selectedDay, time: formattedTime },
        }
      );

      console.log("API Response:", response.data);
      setAvailableRooms(response.data);
    } catch (error) {
      console.error("Error fetching classrooms:", error);
      setAvailableRooms([]);
    }
  };

  return (
    <div className="dashboard-container">
      <h1>Student Dashboard</h1>
      {user ? <h2>Welcome, {user.name}!</h2> : <p>Loading...</p>}

      {/* Selection Form */}
      <div className="selection-container">
        <label htmlFor="day">Day:</label>
        <select
          id="day"
          value={selectedDay}
          onChange={(e) => setSelectedDay(e.target.value)}
        >
          {days.map((day, index) => (
            <option key={index} value={day}>
              {day}
            </option>
          ))}
        </select>

        <label htmlFor="time">Time:</label>
        <select
          id="time"
          value={selectedTime}
          onChange={(e) => setSelectedTime(e.target.value)}
        >
          {timeSlots.map((time, index) => (
            <option key={index} value={time}>
              {time}
            </option>
          ))}
        </select>

        <button className="search-button" onClick={handleSearch}>
          Search
        </button>
      </div>

      {/* Available Rooms */}
      <div className="room-container">
        <h3>Available Rooms</h3>
        {availableRooms.length > 0 ? (
          <div className="room-grid">
            {availableRooms.map((room, index) => (
              <button
                key={index}
                className="room-box"
                onClick={() => handleRoomClick(room)}
              >
                {room["ROOM NUMBER"] || room.roomNumber || "No Room Name"}
              </button>
            ))}
          </div>
        ) : (
          <p>No rooms available for this slot.</p>
        )}
      </div>
    </div>
  );
}

export default StudentDashboard;
