import React, { useEffect, useState } from "react";
import axios from "axios";
import * as XLSX from "xlsx";
import "../css/AdminDAshboard.css"; // Make sure this exists

const AdminDashboard = () => {
  const [activePage, setActivePage] = useState("pending");
  const [bookings, setBookings] = useState([]);
  const [search, setSearch] = useState("");
  const [theme, setTheme] = useState("light"); // ⬅️ Theme state added

  const toggleTheme = () => {
    setTheme((prev) => (prev === "light" ? "dark" : "light"));
  };

  const fetchData = async () => {
    try {
      const routeMap = {
        pending: "/api/admin/pending-bookings",
        approved: "/api/admin/approved-bookings",
        rejected: "/api/admin/rejected-bookings",
      };
      console.log("Requesting:", routeMap[activePage]);
  
      const res = await axios.get(routeMap[activePage]);
      console.log("Received bookings:", res.data);
  
      setBookings(res.data || []);
    } catch (error) {
      console.error("Error fetching bookings:", error?.response || error.message);
      setBookings([]);
    }
  };

  useEffect(() => {
    if (activePage !== "update") fetchData();
  }, [activePage]);

  const handleExport = () => {
    if (bookings.length === 0) return alert("No data to export.");
    const ws = XLSX.utils.json_to_sheet(bookings);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Bookings");
    XLSX.writeFile(wb, `${activePage}_bookings.xlsx`);
  };

  const handleAction = async (action, id) => {
    try {
      const url = `/api/admin/${action}-booking/${id}`;
      await axios.post(url);
      fetchData();
    } catch (error) {
      console.error(`Error during ${action}:`, error);
    }
  };

  const filteredBookings = bookings.filter((booking) =>
    (booking?.classroomNumber || "")
      .toLowerCase()
      .includes(search.toLowerCase())
  );

  return (
    <div className={`dashboard ${theme}`}>
      <aside className="sidebar">
        <h2>Admin Panel</h2>
        <button onClick={() => setActivePage("pending")}>Pending</button>
        <button onClick={() => setActivePage("approved")}>Approved</button>
        <button onClick={() => setActivePage("rejected")}>Rejected</button>
        <button onClick={() => setActivePage("update")}>Update DB</button>

        <div className="toggle-theme">
          <label>
            <input type="checkbox" onChange={toggleTheme} />
            {theme === "light" ? "🌞 Light" : "🌙 Dark"}
          </label>
        </div>
      </aside>

      <main className="main-content">
        {activePage === "update" ? (
          <h1>Update Database Page</h1>
        ) : (
          <>
            <h2>
              {activePage.charAt(0).toUpperCase() + activePage.slice(1)} Requests
            </h2>
            <div className="controls">
              <input
                type="text"
                placeholder="Search by classroom..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
              />
              <button onClick={handleExport}>Export to Excel</button>
            </div>

            <div className="booking-list">
              {filteredBookings.length === 0 ? (
                <p style={{ fontStyle: "italic", marginTop: "20px" }}>
                  No {activePage} bookings found.
                </p>
              ) : (
                filteredBookings.map((booking, index) => (
                  <div className="booking-card" key={booking?._id || index}>
                    <div>
                      <strong>Classroom:</strong> {booking?.classroomNumber || "N/A"}
                    </div>
                    <div>
                      <strong>Teacher ID:</strong> {booking?.teacherId || "N/A"}
                    </div>
                    <div>
                      <strong>Day:</strong> {booking?.day || "N/A"}
                    </div>
                    <div>
                      <strong>Time Slot:</strong> {booking?.timeSlot || "N/A"}
                    </div>
                    <div>
                      <strong>From:</strong>{" "}
                      {booking?.fromDate ? new Date(booking.fromDate).toDateString() : "N/A"}
                    </div>
                    <div>
                      <strong>To:</strong>{" "}
                      {booking?.toDate ? new Date(booking.toDate).toDateString() : "N/A"}
                    </div>
                    <div className={`status ${booking?.status || "unknown"}`}>
                      {booking?.status || "N/A"}
                    </div>

                    {activePage === "pending" && (
                      <div className="actions">
                        <button onClick={() => handleAction("approve", booking._id)}>
                          Approve
                        </button>
                        <button onClick={() => handleAction("reject", booking._id)}>
                          Reject
                        </button>
                      </div>
                    )}
                  </div>
                ))
              )}
            </div>
          </>
        )}
      </main>
    </div>
  );
};

export default AdminDashboard;
