import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import StudentLogin from './components/StudentLogin';
import FacultyLogin from './components/FacultyLogin';
import StudentDashboard from './components/StudentDashboard';
import FacultyDashboard from "./components/FacultyDashboard";
import AdminDashboard from './components/AdminDashboard';
import Home from './components/home';
import AdminLogin from './components/AdminLogin';
import ProtectedRoute from './components/ProtectedRoute'; // âœ… NEW
import SessionGuard from './sessionGuard.js'; // ðŸ‘ˆ NEW

import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';

function App() {
    return (
        <Router>
            <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/std_log" element={<StudentLogin />} />
                <Route path="/fac_log" element={<FacultyLogin />} />
                <Route path="/admin-login" element={<AdminLogin />} />

                {/* âœ… Protected Dashboards */}
                <Route path="/student-dashboard" element={
                    <ProtectedRoute allowedRole="student">
                        <StudentDashboard />
                    </ProtectedRoute>
                } />

                <Route path="/faculty-dashboard" element={
                    <ProtectedRoute allowedRole="faculty">
                        <FacultyDashboard />
                    </ProtectedRoute>
                } />

                <Route path="/admin-dashboard" element={
                    <ProtectedRoute allowedRole="admin">
                        <AdminDashboard />
                    </ProtectedRoute>
                } />
            </Routes>
        </Router>
    );
}

export default App;
