import React, { useState } from 'react';
import axios from 'axios';
import '../css/s_log.css';

function AdminLogin() {
    const [showNotification, setShowNotification] = useState(false);
    const [passwordVisible, setPasswordVisible] = useState(false);
    const [adminID, setAdminID] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const togglePassword = () => {
        setPasswordVisible(!passwordVisible);
    };

    const validateAdminForm = async (e) => {
        e.preventDefault();
        setErrorMessage('');

        if (!adminID.trim() || !password.trim()) {
            setErrorMessage('Both fields are required.');
            setShowNotification(true);
            return;
        }

        try {
            const response = await axios.post("http://localhost:5000/api/auth/admin/login", { adminID, password });

            localStorage.setItem("token", response.data.token);
            localStorage.setItem("role", "admin");

            alert("Login successful!");
            window.location.href = "/admin-dashboard";
        } catch (error) {
            setErrorMessage(error.response?.data?.message || "Login failed. Please try again.");
            setShowNotification(true);
            setTimeout(() => setShowNotification(false), 3000);
        }
    };

    return (
        <div id="admin_log">
            {showNotification && (
                <div className="notification">
                    <i>‚ö†Ô∏è</i> <span>{errorMessage}</span>
                    <span className="close-btn" onClick={() => setShowNotification(false)}>‚úñ</span>
                </div>
            )}

            <div className="c2">
                <h2>Admin Login</h2>
                <form onSubmit={validateAdminForm}>
                    <input type="text" placeholder="Admin ID" value={adminID} onChange={(e) => setAdminID(e.target.value)} />
                    <div className="password-container">
                        <input type={passwordVisible ? 'text' : 'password'} placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
                        <span className="toggle-password" onClick={togglePassword}>üëÅÔ∏è</span>
                    </div>
                    <input type="submit" value="Login" />
                </form>
            </div>
        </div>
    );
}

export default AdminLogin;
