import React, { useEffect, useState } from "react";
import axios from "axios";
import * as XLSX from "xlsx";
import "../css/AdminDashboard.css";

const BASE_URL = "http://localhost:5000";

const AdminDashboard = () => {
  const [activePage, setActivePage] = useState("pending");
  const [bookings, setBookings] = useState([]);
  const [search, setSearch] = useState("");
  const [theme, setTheme] = useState("light");
  const [apiError, setApiError] = useState(false);

  const toggleTheme = () => {
    setTheme((prev) => (prev === "light" ? "dark" : "light"));
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const routeMap = {
          pending: `${BASE_URL}/api/admin/pending-bookings`,
          approved: `${BASE_URL}/api/admin/approved-bookings`,
          rejected: `${BASE_URL}/api/admin/rejected-bookings`,
        };

        if (activePage in routeMap) {
          const res = await axios.get(routeMap[activePage]);
          setApiError(false);
          setBookings(res.data || []);
        }
      } catch (error) {
        console.error("Error fetching bookings:", error?.response || error.message);
        setApiError(true);
        setBookings([]);
      }
    };

    fetchData();
  }, [activePage]);

  const handleExport = () => {
    if (bookings.length === 0) return alert("No data to export.");
    const exportData = bookings.map((b) => ({
      Classroom: b.classroomNumber,
      TeacherID: b.teacherId,
      Day: b.day,
      TimeSlot: b.timeSlot,
      Status: b.status,
      From: new Date(b.fromDate).toLocaleDateString(),
      To: new Date(b.toDate).toLocaleDateString(),
    }));
    const ws = XLSX.utils.json_to_sheet(exportData);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Bookings");
    XLSX.writeFile(wb, `${activePage}_bookings.xlsx`);
  };

  const handleAction = async (action, id) => {
    try {
      await axios.post(`${BASE_URL}/api/admin/${action}-booking/${id}`);
      setTimeout(() => setActivePage("pending"), 200);
    } catch (error) {
      console.error(`Error during ${action}:`, error);
    }
  };

  const filteredBookings = bookings.filter((booking) =>
    (booking?.classroomNumber || "").toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className={`dashboard ${theme}`}>
      <aside className="sidebar">
        <h2>Admin Panel</h2>
        <button onClick={() => setActivePage("pending")}>Pending</button>
        <button onClick={() => setActivePage("approved")}>Approved</button>
        <button onClick={() => setActivePage("rejected")}>Rejected</button>
        <button onClick={() => setActivePage("update")}>Update DB</button>

        <div className="toggle-theme">
          <label>
            <input type="checkbox" onChange={toggleTheme} />
            {theme === "light" ? "🌞 Light" : "🌙 Dark"}
          </label>
        </div>
      </aside>

      <main className="main-content">
        {activePage === "update" ? (
          <div>
            <h2>Update Database Page</h2>
            <p style={{ fontStyle: "italic" }}>
              This is a placeholder for database updates or maintenance operations.
            </p>
          </div>
        ) : (
          <>
            <h2>{activePage.charAt(0).toUpperCase() + activePage.slice(1)} Requests</h2>

            <div className="controls">
              <input
                type="text"
                placeholder="Search by classroom..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
              />
              <button onClick={handleExport}>Export to Excel</button>
            </div>

            <div className="booking-list">
              {apiError ? (
                <p className="error-message">
                  ⚠️ Failed to load data. Please check the server.
                </p>
              ) : filteredBookings.length === 0 ? (
                <p className="no-data">No {activePage} bookings found.</p>
              ) : (
                filteredBookings.map((booking, index) => (
                  <div className="booking-card" key={booking?._id || index}>
                    <p><strong>Classroom:</strong> {booking.classroomNumber}</p>
                    <p><strong>Teacher ID:</strong> {booking.teacherId}</p>
                    <p><strong>Day:</strong> {booking.day}</p>
                    <p><strong>Time Slot:</strong> {booking.timeSlot}</p>
                    <p><strong>From:</strong> {new Date(booking.fromDate).toDateString()}</p>
                    <p><strong>To:</strong> {new Date(booking.toDate).toDateString()}</p>

                    <span className={`status-badge ${booking.status}`}>
                      {booking.status}
                    </span>

                    {activePage === "pending" && (
                      <div className="action-buttons">
                        <button onClick={() => handleAction("approve", booking._id)}>Approve</button>
                        <button onClick={() => handleAction("reject", booking._id)}>Reject</button>
                      </div>
                    )}
                  </div>
                ))
              )}
            </div>
          </>
        )}
      </main>
    </div>
  );
};

export default AdminDashboard;
