import React, { useState, useEffect } from "react";
import axios from "axios";
import "../css/studentDashboard.css";

//const handleRoomClick = (room) => {
//  alert(`You clicked on ${room["ROOM NUMBER"] || room.roomNumber}`);
//};

function StudentDashboard() {
  const [user, setUser] = useState(null);
  const [selectedDay, setSelectedDay] = useState("Monday");
  const [selectedTime, setSelectedTime] = useState("8:00 AM - 8:50 AM");
  const [availableRooms, setAvailableRooms] = useState([]);
  const [filteredRooms, setFilteredRooms] = useState([]);
  const [selectedBlocks, setSelectedBlocks] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  const days = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday"];
  const timeSlots = [
    "8:00 AM - 8:50 AM",
    "9:00 AM - 9:50 AM",
    "10:00 AM - 10:50 AM",
    "11:00 AM - 11:50 AM",
    "12:00 PM - 12:50 PM",
    "2:00 PM - 2:50 PM",
    "3:00 PM - 3:50 PM",
    "4:00 PM - 4:50 PM",
    "5:00 PM - 5:50 PM",
    "6:00 PM - 6:50 PM",
  ];

  const blocks = ["SJT", "TT", "MGB", "ALM", "CDMM", "GDN", "PRP", "CTS", "SMV", "MB"];

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      try {
        const payload = JSON.parse(
          decodeURIComponent(escape(atob(token.split(".")[1])))
        );
        setUser(payload);
      } catch (error) {
        console.error("Error decoding token:", error);
      }
    }
  }, []);

  const handleBlockCheckboxChange = (block) => {
    if (selectedBlocks.includes(block)) {
      setSelectedBlocks(selectedBlocks.filter((b) => b !== block));
    } else {
      setSelectedBlocks([...selectedBlocks, block]);
    }
  };

  const handleSearch = async () => {
    try {
      const response = await axios.get(
        "http://localhost:5000/api/classrooms/available-rooms",
        {
          params: { day: selectedDay, time: selectedTime },
        }
      );

      const allRooms = response.data;

      const filtered = selectedBlocks.length > 0
        ? allRooms.filter((room) => {
            const roomStr = (room["ROOM NUMBER"] || room.roomNumber || "").toUpperCase();
            const blockMatch = roomStr.match(/^[A-Z]+/);
            const block = blockMatch ? blockMatch[0] : "";
            return selectedBlocks.includes(block);
          })
        : allRooms;

      setAvailableRooms(allRooms);
      setFilteredRooms(filtered);
    } catch (error) {
      console.error("Error fetching classrooms:", error);
      setAvailableRooms([]);
      setFilteredRooms([]);
    }
  };

  const handleReset = () => {
    setSelectedDay("Monday");
    setSelectedTime("8:00 AM - 8:50 AM");
    setSelectedBlocks([]);
    setSearchTerm("");
    setAvailableRooms([]);
    setFilteredRooms([]);
  };

  // Group filtered rooms by block
  const groupedRooms = filteredRooms.reduce((acc, room) => {
    const roomStr = (room["ROOM NUMBER"] || room.roomNumber || "").toUpperCase();
    const blockMatch = roomStr.match(/^[A-Z]+/);
    const block = blockMatch ? blockMatch[0] : "Others";
    if (!acc[block]) acc[block] = [];
    acc[block].push(room);
    return acc;
  }, {});
  
  const [isChatOpen, setIsChatOpen] = useState(false);

  const toggleChat = () => {
    setIsChatOpen((prev) => !prev);
  };
  const [message, setMessage] = useState("");
const [messages, setMessages] = useState([]);

const handleSendMessage = () => {
  if (message.trim() === "") return;

  // Save the message in the local message array (for now)
  setMessages([...messages, message]);

  // TODO: Send message to admin via backend API here

  setMessage(""); // clear input
};

  
  return (
    <div className="dashboard-wrapper">
      <div className="dashboard-container">
      <h1>Student Dashboard</h1>
      {user ? <h2>Welcome, {user.name}!</h2> : <p>Loading...</p>}

      {/* Selection Form */}
      <div className="selection-container">
        <label htmlFor="day">Day:</label>
        <select
          id="day"
          value={selectedDay}
          onChange={(e) => setSelectedDay(e.target.value)}
        >
          {days.map((day, index) => (
            <option key={index} value={day}>
              {day}
            </option>
          ))}
        </select>

        <label htmlFor="time">Time:</label>
        <select
          id="time"
          value={selectedTime}
          onChange={(e) => setSelectedTime(e.target.value)}
        >
          {timeSlots.map((time, index) => (
            <option key={index} value={time}>
              {time}
            </option>
          ))}
        </select>

        <button className="search-button" onClick={handleSearch}>
          Search
        </button>

        <button className="reset-button" onClick={handleReset}>
          Reset
        </button>
      </div>

      {/* Block Filters as Checkboxes */}
      <div className="block-filter">
        {blocks.map((block, index) => (
          <label key={index} className="block-checkbox">
            <input
              type="checkbox"
              value={block}
              checked={selectedBlocks.includes(block)}
              onChange={() => handleBlockCheckboxChange(block)}
            />
            {block}
          </label>
        ))}
      </div>

      {/* Search Bar */}
      <div className="search-bar-container">
        <input
          type="text"
          placeholder="Search room number..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="room-search-input"
        />
      </div>

      {/* Available Rooms grouped by block */}
      <div className="room-container">
        <h3>Available Rooms</h3>
        {Object.keys(groupedRooms).length > 0 ? (
          Object.entries(groupedRooms).map(([block, rooms]) => {
            const filteredBySearch = rooms.filter((room) =>
              (room["ROOM NUMBER"] || room.roomNumber || "")
                .toLowerCase()
                .includes(searchTerm.toLowerCase())
            );
            if (filteredBySearch.length === 0) return null;
            return (
              <div key={block} className="block-group">
                <h4>{block}</h4>
                <div className="room-grid">
                  {filteredBySearch.map((room, idx) => (
                    <button
                      key={idx}
                      className="room-box"
                     // onClick={() => handleRoomClick(room)}
                    >
                      {room["ROOM NUMBER"] || room.roomNumber || "No Room Name"}
                    </button>
                  ))}
                </div>
              </div>
            );
          })
        ) : (
          <p>No rooms available for this slot.</p>
        )}
      </div>
    </div>
{/* Chat Toggle Button */}
<div className="chat-box" onClick={toggleChat}>
</div>

{isChatOpen && (
  <div className="chat-popup">
    <div className="chat-header">
      <span>ChatBot</span>
      <span className="close-btn" onClick={toggleChat}>Ã—</span>
    </div>
    <div className="chat-body">
      {messages.map((msg, index) => (
        <div key={index} className="chat-message user">{msg}</div>
      ))}
    </div>
    <div className="chat-input-container">
      <input
        type="text"
        placeholder="Type your message..."
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        onKeyDown={(e) => {
          if (e.key === "Enter") handleSendMessage();
        }}
        className="chat-input"
      />
      <button onClick={handleSendMessage} className="chat-send-btn">Send</button>
    </div>
  </div>
)}


    </div>

);
}

export default StudentDashboard;
