import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../css/admin_login.css'; // üëà Rename your new cool CSS here
import eye1 from '../css/eye1.png';
import eye2 from '../css/eye2.png';

function AdminLogin() {
    const navigate = useNavigate();

    const [showNotification, setShowNotification] = useState(false);
    const [passwordVisible, setPasswordVisible] = useState(false);
    const [adminID, setAdminID] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [loading, setLoading] = useState(false);

    const togglePassword = () => {
        setPasswordVisible(!passwordVisible);
    };

    const validateAdminForm = async (e) => {
        e.preventDefault();
        setErrorMessage('');
        setShowNotification(false);
    
        if (!adminID.trim() || !password.trim()) {
            setErrorMessage('Both fields are required.');
            setShowNotification(true);
            return;
        }
    
        try {
            setLoading(true);
    
            const response = await axios.post("http://localhost:5000/api/auth/admin/login", { adminID, password });
    
            localStorage.setItem("token", response.data.token);
            localStorage.setItem("role", "admin");
    
            // alert("Login successful!"); ‚Üê Removed this line
            navigate("/admin-dashboard");
    
        } catch (error) {
            setErrorMessage(error.response?.data?.message || "Login failed. Please try again.");
            setShowNotification(true);
            setTimeout(() => setShowNotification(false), 3000);
        } finally {
            setLoading(false);
        }
    };
    

    return (
        <div id="admin_log">
            {showNotification && (
                <div className="notification">
                    <i>‚ö†Ô∏è</i> <span>{errorMessage}</span>
                    <span className="close-btn" onClick={() => setShowNotification(false)}>‚úñ</span>
                </div>
            )}

            <div className="c2">
                <h2>Admin Login</h2>
                <form onSubmit={validateAdminForm}>
                    <label htmlFor="user">Admin ID</label>
                    <input
                        id="user"
                        type="text"
                        placeholder="Enter Admin ID"
                        value={adminID}
                        onChange={(e) => setAdminID(e.target.value)}
                    />

                   
<label htmlFor="password">Password</label>
<div className="password-container">
  <input
    id="password"
    type={passwordVisible ? 'text' : 'password'}
    placeholder="Enter Password"
    value={password}
    onChange={(e) => setPassword(e.target.value)}
  />
  <img
    src={passwordVisible ? eye2 : eye1}
    alt="Toggle visibility"
    className="toggle-password"
    onClick={togglePassword}
  />
</div>

                    <input
                        type="submit"
                        value={loading ? "Logging in..." : "Login"}
                        disabled={loading}
                    />
                </form>
            </div>
        </div>
    );
}

export default AdminLogin;
