import React, { useState } from 'react';
import axios from 'axios';
import '../css/s_log.css';

function StudentLogin() {
    const [showNotification, setShowNotification] = useState(false);
    const [passwordVisible, setPasswordVisible] = useState(false);
    const [Regno, setRegno] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const togglePassword = () => {
        setPasswordVisible(!passwordVisible);
    };

    const validateStudentForm = async (e) => {
        e.preventDefault();
        setErrorMessage('');

        if (!Regno.trim() || !password.trim()) {
            setErrorMessage('Both fields are required.');
            setShowNotification(true);
            return;
        }

        try {
            const response = await axios.post("http://localhost:5000/api/auth/student-login", {
                Regno,  // Ensure this matches the backend schema
                password
            });
            

            localStorage.setItem("token", response.data.token);
            localStorage.setItem("role", "student");

            alert("Login successful!");
            window.location.href = "/student-dashboard";
        } catch (error) {
            setErrorMessage(error.response?.data?.message || "Login failed. Please try again.");
            setShowNotification(true);
            setTimeout(() => setShowNotification(false), 3000);
        }
    };

    return (
        <div id="std_log">
            {showNotification && (
                <div className="notification">
                    <i>‚ö†Ô∏è</i> <span>{errorMessage}</span>
                    <span className="close-btn" onClick={() => setShowNotification(false)}>‚úñ</span>
                </div>
            )}

            <div className="c2">
                <h2>Student Login</h2>
                <form onSubmit={validateStudentForm}>
                    <input type="text" placeholder="Registration Number" value={regno} onChange={(e) => setRegno(e.target.value)} />
                    <div className="password-container">
                        <input type={passwordVisible ? 'text' : 'password'} placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
                        <span className="toggle-password" onClick={togglePassword}>üëÅÔ∏è</span>
                    </div>
                    <a href="/forgot-password">Forgot Password?</a>
                    <input type="submit" value="Login" />
                </form>
            </div>
        </div>
    );
}

export default StudentLogin;
